import React, { useEffect, useState } from "react";
import "./banner.css";
import axios from "../../Axios";
import { API_KEY } from "../../constants/constants";

function Banner() {
  const [state, setMovie] = useState()
  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/trending/all/week?api_key=${API_KEY}&language=en-US`
      )
      .then((response) => {
        console.log(response.data.results[0]);
        setMovie(response.data.results[0])
      });
  }, []);
  return (
    <div className="banner">
      <div className="content">
        <h1 className="title">Movie Name</h1>
        <div className="banner-button">
          <button className="button">Play</button>
          <button className="button">My List</button>
        </div>
        <h1 className="description">
          Netflix is one of the world's leading entertainment services with 223
          million paid memberships in over 190 countries enjoying TV series,
          documentaries, feature films and mobile games across a wide variety of
          genres and languages. Members can play, pause and resume to watch, as
          much as they want, anytime, anywhere, and can change their plans at
          any time.
        </h1>
      </div>
      <div className="fade-bottom"></div>
    </div>
  );
}

export default Banner;
